/**
 * Get random motivational quotes
 * @param {string} lang - 'en' | 'ru' | 'cn'
 * @param {number} count - how many quotes to return
 * @returns {Array<{text: string, author: string}>}
 */
function getRandomQuotes(lang, count) {
    const shuffled = [...MOTIVATION_QUOTES].sort(() => Math.random() - 0.5);
    return shuffled.slice(0, count).map(q => ({
        text: q[lang] || q.en,
        author: q.author || ''
    }));
}

/**
 * Get random jump principles
 * @param {string} lang - 'en' | 'ru' | 'cn'
 * @param {number} count - how many principles to return
 * @returns {Array<{text: string, icon: string}>}
 */
function getRandomPrinciples(lang, count) {
    const shuffled = [...JUMP_PRINCIPLES].sort(() => Math.random() - 0.5);
    return shuffled.slice(0, count).map(p => ({
        text: p[lang] || p.en,
        icon: p.icon || 'ğŸ’¡'
    }));
}

/**
 * Build motivational HTML block for PDFs
 * @param {string} lang - 'en' | 'ru' | 'cn'
 * @param {number} quoteCount - default 3
 * @param {number} principleCount - default 2
 * @returns {string} HTML string
 */
function buildMotivationBlock(lang) {
    const showQuote = Math.random() < 0.5;

    let html = `<div class="motivation-block">`;

    if (showQuote) {
        const q = getRandomQuotes(lang, 1)[0];
        html += `<div class="motivation-quote">`;
        html += `<span class="mq-text">"${q.text}"</span>`;
        if (q.author) html += `<span class="mq-author">â€” ${q.author}</span>`;
        html += `</div>`;
    } else {
        const p = getRandomPrinciples(lang, 1)[0];
        html += `<div class="motivation-principle">`;
        html += `<span class="mp-icon">${p.icon}</span>`;
        html += `<span class="mp-text">${p.text}</span>`;
        html += `</div>`;
    }

    html += `</div>`;
    return html;
}
